mapper.select:
SELECT 
FROM lottery_records lr
LEFT JOIN prizes p ON lr.prize_id = p.id
WHERE lr.user_id = #{userId}
ORDER BY lr.created_at DESC
LIMIT #{limit} OFFSET #{offset}

