mapper.select:
SELECT id, code, name, description, daily_limit, remaining_uses, energy_cost, last_refresh_at, created_at
FROM magic
WHERE code = #{code}

mapper.update:
UPDATE magic
SET remaining_uses = #{remainingUses},
    last_refresh_at = #{lastRefreshAt}
WHERE code = #{code}

UPDATE magic
SET energy_cost = #{energyCost}
WHERE code = #{code}
